#!/usr/bin/env node
import { zip } from "zip-a-folder";
import fs from "fs";

async function createZip() {
  const outputDir = "./dist";
  const zipFile = `${outputDir}/keyboard_layout_translator.zip`;

  if (!fs.existsSync(outputDir)) {
    fs.mkdirSync(outputDir);
  }

  try {
    await zip("./src", zipFile);
    console.log("Розширення успішно зібрано:", zipFile);
  } catch (error) {
    console.error("Помилка під час створення архіву:", error);
  }
}

createZip();
